import React from 'react';
import '../assets/styles/components/Border.css';
import '../assets/styles/components/DropArea.css';
import axios from 'axios';
import {useState,useEffect} from 'react';


function onChange(e) {
    let files = e.target.files;
    let reader = new FileReader();
    reader.readAsDataURL(files[0]);


    reader.onload=(e)=> {
        const url = "127.0.0.1:8000/api/file"
        const formData = { file:e.target.result };
        return axios.post(url, formData).then(response => console.warn("result", response));
    }
}
function Border() {
    const [isLoading, setLoading] = useState(true);

  function fakeRequest() {
    return new Promise(resolve => setTimeout(() => resolve(), 2500));
  }

  useEffect(() => {
    fakeRequest().then(() => {
      const el = document.querySelector(".loader-container");
      if (el) {
        el.remove();
        setLoading(!isLoading);
      }
    });
  }, []);

  if (isLoading) {
    return null;
  }

    return (
        <div className="border">
            NOMIS
            <div className="drop_area">
                <input type="file" name="file" onChange={(e)=>onChange(e)} />
            </div>
            <div className="status">
                Process Status
                <ul>
                    <li>Uploading file</li>
                    <li>Pausing queues</li>
                    <li>Resume queues</li>
                    <li>Restarting API</li>
                </ul>
            </div>
        </div>)
};



export default Border;
